mapper.update:
UPDATE VIDEO
SET video = #{video.video}, video_thumnail = #{video.videoThumnail}, title = #{video.title}, content = #{video.content}
WHERE video_seq = #{video.videoSeq}

UPDATE VIDEO
SET publish_yn = #{video.publishYn}
WHERE video_seq = #{video.videoSeq}

UPDATE VIDEO
SET delete_yn = #{video.deleteYn}
WHERE video_seq = #{video.videoSeq}
